const hiddenCode =
  "XXXXXXXXXXXXXXXXXXXXXXXXXXXCXXXXXXXOXXXXXDXXXXXXXXXXXXXXXEXXXX XXXXXXXXXXXXXXX:";

tab = hiddenCode.split("");
message=""
for (let i = 0; i < tab.length; i++) {
  if (tab[i] == "X") continue;
  if (parseInt(tab[i]) % 2 == 0) continue;
  message = message + tab[i];
}
console.log(message)
